import json

class packet_config:
    def __init__(self, constraints = None):
        #init values
        self.trill = 1
        self.vlan  = 4
        self.mpls  = 4
        self.constraints = None
  
        #configure
        if (constraints != None):
            self.constraints = json.loads(constraints);

        mpls_stack = self.object_get(["mpls", "stack"]);
        if (mpls_stack != None):
            self.mpls = int(mpls_stack.get("max"));

        vlan_stack = self.object_get(["vlan", "stack"]);
        if (vlan_stack != None):
            self.vlan = int(vlan_stack.get("max"));

    def copy(self):
        ret = packet_config()
        ret.trill = self.trill
        ret.vlan = self.vlan
        ret.mpls = self.mpls
        ret.constraints = self.constraints
        return ret;

    def object_get(self, path):
        index = 0
        obj   = self.constraints
        while (index < len(path) and obj != None):
            obj = obj.get(path[index])
            index += 1

        return obj;
